package com.example.www;

public class EvenOddNumber {


	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number;
		number=881;
		int remainder;
		remainder=number%2;
		
		if(remainder==0) {
			System.out.println(number +" is even");
		}
		else {
			System.out.println(number+" is odd");
		}

	}
	

}
